import React from 'react';
import { Swiper, SwiperSlide } from 'swiper/react';
import { Pagination, Autoplay, Navigation } from 'swiper';
import 'swiper/css';
import 'swiper/css/pagination';
import 'swiper/css/autoplay';
import 'swiper/css/navigation';
import './SwiperPage.css';

const images = [
    { src: '/uploads/image1.jpg', description: 'Description for image 1' },
    { src: '/uploads/image2.jpg', description: 'Description for image 2' },
    { src: '/uploads/image3.jpg', description: 'Description for image 3' },
    { src: '/uploads/image4.jpg', description: 'Description for image 4' },
    { src: '/uploads/image5.jpg', description: 'Description for image 5' },
    { src: '/uploads/image6.jpg', description: 'Description for image 6' },
    { src: '/uploads/image7.jpg', description: 'Description for image 7' },
    { src: '/uploads/image8.jpg', description: 'Description for image 8' },
    { src: '/uploads/image9.jpg', description: 'Description for image 9' }
];

const SwiperPage = () => {
    return (
        <div className="swiper-page">
            <div className="swiper-page__slideshow-container">
                <div className="swiper-page__slideshow-description">
                    <h2>About the Project</h2>
                    <p>Most of the important things in the world have been accomplished by people who have kept on trying when there seemed to be no hope at all.</p>
                    <a href="#">View more</a>
                </div>
                <Swiper
                    spaceBetween={30}
                    slidesPerView={2.5}
                    loop={true}
                    pagination={{ clickable: true, el: '.swiper-page__custom-pagination' }}
                    autoplay={{ delay: 5000 }}
                    grabCursor={true}
                    navigation={{
                        nextEl: '.swiper-page__custom-next',
                        prevEl: '.swiper-page__custom-prev'
                    }}
                    modules={[Pagination, Autoplay, Navigation]}
                    className="swiper-page__swiper-container"
                >
                    {images.map((image, index) => (
                        <SwiperSlide key={index} className="swiper-page__swiper-slide">
                            <div className="swiper-page__slide-content">
                                <img src={image.src} alt={`slide-${index}`} className="swiper-page__slide-image" />
                                <div className="swiper-page__slide-description">{image.description}</div>
                            </div>
                        </SwiperSlide>
                    ))}
                </Swiper>
                <div className="swiper-page__custom-pagination"></div>
            </div>
        </div>
    );
};

export default SwiperPage;
