import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate, useParams } from 'react-router-dom';
import './RequestDetailPage.css';

const RequestDetailPage = () => {
    const [request, setRequest] = useState(null);
    const { id } = useParams();
    const navigate = useNavigate();

    useEffect(() => {
        const fetchRequest = async () => {
            try {
                const response = await axios.get(`/api/requests/${id}`);
                setRequest(response.data);
            } catch (error) {
                console.error('Error fetching request', error);
            }
        };

        fetchRequest();
    }, [id]);

    const handleBackToList = () => {
        navigate('/request');
    };

    const handleEdit = () => {
        navigate(`/request/edit/${id}`);
    };

    if (!request) {
        return <div>Loading...</div>;
    }

    return (
        <div className="request-detail-page-wrapper">
            <div className="request-detail-page">
                <h1>{request.title}</h1>
                <div className="detail-item">
                    <strong>Category:</strong> <span>{request.category}</span>
                </div>
                <div className="detail-item">
                    <strong>Name:</strong> <span>{request.name}</span>
                </div>
                <div className="detail-item">
                    <strong>Email:</strong> <span>{request.email}</span>
                </div>
                <div className="detail-item">
                    <strong>Phone:</strong> <span>{request.phone}</span>
                </div>
                <div className="detail-item">
                    <strong>Content:</strong> <span>{request.content}</span>
                </div>
                <div className="buttons">
                    <button className="back-button" onClick={handleBackToList}>Back to List</button>
                    <button className="edit-button" onClick={handleEdit}>Edit</button>
                </div>
            </div>
        </div>
    );
};

export default RequestDetailPage;
