import React, { useEffect } from 'react';
import SwiperPage from './SwiperPage';
import './MainPage.css';
import AboutPage from "./AboutPage";
import PortfolioPage from "./PortfolioPage";
import ContactPage from "./ContactPage";
import CategoryListPage from './CategoryListPage';

const MainPage = () => {
    useEffect(() => {
        const video = document.querySelector('.background-video');

        const resizeVideo = () => {
            const aspectRatio = 16 / 9;
            const windowRatio = window.innerWidth / window.innerHeight;

            if (windowRatio < aspectRatio) {
                video.style.width = 'auto';
                video.style.height = '100%';
            } else {
                video.style.width = '100%';
                video.style.height = 'auto';
            }
        };

        const startVideoAtFiveSeconds = () => {
            video.currentTime = 5; // 비디오 시작 시간을 5초로 설정
            video.play();
        };

        // 초기 비디오 크기 조정 및 시작 시간 설정
        resizeVideo();
        startVideoAtFiveSeconds();

        // 창 크기 변경 시 비디오 크기 조정
        window.addEventListener('resize', resizeVideo);

        // 컴포넌트 언마운트 시 이벤트 리스너 제거
        return () => {
            window.removeEventListener('resize', resizeVideo);
        };
    }, []);

    return (
        <div className="main-page">
            <div className="video-container">
                <video autoPlay loop muted className="background-video">
                    <source src="./uploads/video.mp4" type="video/mp4"/>
                </video>
                <div className="main-page-content">
                    <h1>Cinemadoc,<br/>where the imagination becomes reality.</h1>
                </div>
            </div>
            <div className="swiper-wrapper">
                <SwiperPage/>
            </div>
            <div className="section about-section">
                <AboutPage/>
            </div>
            <div className="section portfolio-section">
                <PortfolioPage/>
            </div>
            <div className="section request-section">
                <CategoryListPage/>
            </div>
            <div className="section contact-section">
                <ContactPage/>
            </div>
        </div>
    );
};

export default MainPage;
