import React, { useState, useEffect } from 'react';
import { useLocation, useParams } from 'react-router-dom';
import axios from 'axios';
import './RequestFormPage.css';

const RequestFormPage = () => {
    const [form, setForm] = useState({
        category: 'Animation',
        name: '',
        password: '',
        phone: '',
        email: '',
        title: '',
        content: '',
    });

    const location = useLocation();
    const { id } = useParams();
    const isEdit = Boolean(id);

    useEffect(() => {
        const query = new URLSearchParams(location.search);
        const category = query.get('category') || 'Animation';
        setForm((prevForm) => ({ ...prevForm, category }));

        if (isEdit) {
            fetchRequestData();
        }
    }, [location.search, id]);

    const fetchRequestData = async () => {
        try {
            const response = await axios.get(`/api/requests/${id}`);
            setForm(response.data);
        } catch (error) {
            console.error('Error fetching request data', error);
        }
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setForm({ ...form, [name]: value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            if (isEdit) {
                await axios.put(`/api/requests/${id}`, form);
            } else {
                await axios.post('/api/requests', form);
            }
            setForm({
                category: 'Animation',
                name: '',
                password: '',
                phone: '',
                email: '',
                title: '',
                content: '',
            });
        } catch (error) {
            console.error('Error submitting request', error);
        }
    };

    return (
        <div className="request-form-page">
            <h1>{isEdit ? `Edit ${form.category} Request` : `Submit a ${form.category} Request`}</h1>
            <form onSubmit={handleSubmit} className="request-form">
                <select name="category" value={form.category} onChange={handleChange} required>
                    <option value="Animation">Animation</option>
                    <option value="Movie">Movie</option>
                    <option value="Game">Game</option>
                </select>
                <input type="text" name="name" value={form.name} onChange={handleChange} placeholder="Name" required />
                <input type="password" name="password" value={form.password} onChange={handleChange} placeholder="Password" required />
                <input type="text" name="phone" value={form.phone} onChange={handleChange} placeholder="Phone" required />
                <input type="email" name="email" value={form.email} onChange={handleChange} placeholder="Email" required />
                <input type="text" name="title" value={form.title} onChange={handleChange} placeholder="Title" required />
                <textarea name="content" value={form.content} onChange={handleChange} placeholder="Content" required></textarea>
                <button type="submit">{isEdit ? 'Update Request' : 'Submit Request'}</button>
            </form>
        </div>
    );
};

export default RequestFormPage;
