import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './CategoryListPage.css';

const CategoryListPage = () => {
    const [requests, setRequests] = useState([]);
    const [category, setCategory] = useState('Animation');
    const navigate = useNavigate();

    useEffect(() => {
        fetchRequests();
    }, [category]);

    const fetchRequests = async () => {
        try {
            const response = await axios.get('/api/requests');
            setRequests(response.data.filter(req => req.category === category));
        } catch (error) {
            console.error('Error fetching requests', error);
        }
    };

    const handleCategoryClick = (selectedCategory) => {
        setCategory(selectedCategory);
        fetchRequests(); // 카테고리 변경 시 목록을 다시 가져옴
    };

    const handleNewRequestClick = (selectedCategory) => {
        navigate(`/request/new?category=${selectedCategory}`);
    };

    return (
        <div className="category-list-page">
            <h1>Request</h1>
            <h2>Asset & LookDev</h2>
            <div className="categories">
                <button className="category-button" onClick={() => handleCategoryClick('Animation')}>Animation</button>
                <button className="category-button" onClick={() => handleCategoryClick('Movie')}>Movie</button>
                <button className="category-button" onClick={() => handleCategoryClick('Game')}>Game</button>
            </div>
            <div className="request-list">
                {requests.map((request) => (
                    <div key={request._id} className="request-item">
                        <span className="category-icon">{request.category}</span>
                        <span className="request-title">{request.title}</span>
                        <span className="request-author">{request.name}</span>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default CategoryListPage;
