import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './RequestPage.css';

const RequestPage = () => {
    const [requests, setRequests] = useState([]);
    const [form, setForm] = useState({
        category: '',
        name: '',
        password: '',
        phone: '',
        email: '',
        title: '',
        content: '',
    });

    useEffect(() => {
        fetchRequests();
    }, []);

    const fetchRequests = async () => {
        try {
            const response = await axios.get('/api/requests');
            setRequests(response.data);
        } catch (error) {
            console.error('Error fetching requests', error);
        }
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setForm({ ...form, [name]: value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await axios.post('/api/requests', form);
            fetchRequests();
            setForm({
                category: '',
                name: '',
                password: '',
                phone: '',
                email: '',
                title: '',
                content: '',
            });
        } catch (error) {
            console.error('Error submitting request', error);
        }
    };

    return (
        <div className="request-page">
            <div className="request-content">
                <h1>Asset & LookDev</h1>
                <div className="categories">
                    <button onClick={() => setForm({ ...form, category: 'Animation' })}>Animation</button>
                    <button onClick={() => setForm({ ...form, category: 'Movie' })}>Movie</button>
                    <button onClick={() => setForm({ ...form, category: 'Game' })}>Game</button>
                </div>
                <div className="request-form">
                    <form onSubmit={handleSubmit}>
                        <input type="text" name="name" value={form.name} onChange={handleChange} placeholder="Name" required />
                        <input type="password" name="password" value={form.password} onChange={handleChange} placeholder="Password" required />
                        <input type="text" name="phone" value={form.phone} onChange={handleChange} placeholder="Phone" required />
                        <input type="email" name="email" value={form.email} onChange={handleChange} placeholder="Email" required />
                        <input type="text" name="title" value={form.title} onChange={handleChange} placeholder="Title" required />
                        <textarea name="content" value={form.content} onChange={handleChange} placeholder="Content" required></textarea>
                        <button type="submit">Submit</button>
                    </form>
                </div>
                <div className="request-list">
                    {requests.map((request) => (
                        <div key={request._id} className="request-item">
                            <span className="category-icon">{request.category}</span>
                            <span className="request-title">{request.title}</span>
                            <span className="request-author">{request.name}</span>
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
};

export default RequestPage;
